        // 4RGBA156RGBA2,2,1,1
        // RGBA
        let div = usingClipCount / ColorChannelCount; // 1
        let mod = usingClipCount % ColorChannelCount; // 
        // 
        div = ~~div;
        mod = ~~mod;
